MCMRendererDialog:
{
  "type": "panel",
  "children": [
    {
      "type": "field",
      "label": "Extinction:",
      "children": [
        {
          "type": "spinner",
          "bind": "extinction",
          "logarithmic": true,
          "value": 1,
          "min": 0,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Scattering albedo:",
      "children": [
        {
          "type": "slider",
          "bind": "albedo",
          "value": 0.5,
          "min": 0,
          "max": 1,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Scattering bias:",
      "children": [
        {
          "type": "slider",
          "bind": "bias",
          "value": 0,
          "min": -1,
          "max": 1,
          "step": 0.2
        }
      ]
    },
    {
      "type": "field",
      "label": "Majorant ratio:",
      "children": [
        {
          "type": "slider",
          "bind": "ratio",
          "value": 1,
          "min": 0,
          "max": 1,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Max bounces:",
      "children": [
        {
          "type": "spinner",
          "bind": "bounces",
          "value": 8,
          "min": 0
        }
      ]
    },
    {
      "type": "field",
      "label": "Steps:",
      "children": [
        {
          "type": "spinner",
          "bind": "steps",
          "value": 8,
          "min": 1
        }
      ]
    },
    {
      "type": "accordion",
      "label": "Transfer function",
      "children": [
        {
          "type": "panel",
          "bind": "tfcontainer"
        }
      ]
    }
  ]
}

RCRendererDialog:
{
  "type": "panel",
  "children": [
    {
      "type": "field",
      "label": "Steps:",
      "children": [
        {
          "type": "spinner",
          "bind": "steps",
          "value": 250,
          "min": 1
        }
      ]
    },
    {
      "type": "field",
      "label": "Opacity:",
      "children": [
        {
          "type": "spinner",
          "bind": "opacity",
          "logarithmic": true,
          "value": 10.0,
          "min": 0,
          "step": 0.1
        }
      ]
    },
    {
      "type": "field",
      "label": "Randomize sampling:",
      "children": [
        {
          "type": "checkbox",
          "bind": "randomize",
          "checked": true
        }
      ]
    },
    {
      "type": "field",
      "label": "Reflection model:",
      "children": [
        {
          "type": "dropdown",
          "bind": "reflectionModelSelect",
          "options": [
            {
              "value": "lambertian",
              "label": "Lambertian",
              "selected": true
            },
            {
              "value": "phong",
              "label": "Phong"
            }
          ]
        }
      ]
    },
    {
      "type": "accordion",
      "label": "Lights",
      "children": [
        {
          "type": "field",
          "label": "Light type:",
          "children": [
            {
              "type": "dropdown",
              "bind": "lightSelect",
              "options": [
                {
                  "value": "ambient",
                  "label": "Ambient Light",
                  "selected": true
                },
                {
                  "value": "directed",
                  "label": "Directed Light"
                },
                {
                  "value": "point",
                  "label": "Point Light"
                }
              ]
            }
          ]
        },
        {
          "type": "panel",
          "bind": "lightSettingsContainer"
        }
      ]
    },
    {
      "type": "accordion",
      "label": "Transfer function",
      "children": [
        {
          "type": "panel",
          "bind": "tfcontainer"
        }
      ]
    }
  ]
}

MainDialog:
{
  "type": "sidebar",
  "bind": "sidebar",
  "children": [
    {
      "type": "tabs",
      "children": {
        "Data": {
          "type": "panel",
          "children": [
            {
              "type": "accordion",
              "bind": "volumeLoadContainer",
              "label": "Volume"
            },
            {
              "type": "accordion",
              "bind": "envmapLoadContainer",
              "label": "Environment"
            }
          ]
        },
        "Settings": {
          "type": "panel",
          "children": [
            {
              "type": "accordion",
              "label": "Renderer",
              "children": [
                {
                  "type": "field",
                  "label": "Renderer:",
                  "children": [
                    {
                      "type": "dropdown",
                      "bind": "rendererSelect",
                      "options": [
                        {
                          "value": "rc",
                          "label": "Ray Casting",
                          "selected": true
                        },
                        {
                          "value": "mip",
                          "label": "Maximum intensity projection"
                        },
                        {
                          "value": "iso",
                          "label": "Isosurface extraction"
                        },
                        {
                          "value": "eam",
                          "label": "Emission-absorption model"
                        }
                      ]
                    }
                  ]
                },
                {
                  "type": "panel",
                  "bind": "rendererSettingsContainer"
                }
              ]
            },
            {
              "type": "accordion",
              "label": "Tone mapper",
              "children": [
                {
                  "type": "field",
                  "label": "Tone mapper:",
                  "children": [
                    {
                      "type": "dropdown",
                      "bind": "toneMapperSelect",
                      "options": [
                        {
                          "value": "range",
                          "label": "Range"
                        },
                        {
                          "value": "reinhard",
                          "label": "Reinhard"
                        },
                        {
                          "value": "artistic",
                          "label": "Artistic",
                          "selected": true
                        }
                      ]
                    }
                  ]
                },
                {
                  "type": "panel",
                  "bind": "toneMapperSettingsContainer"
                }
              ]
            },
            {
              "type": "accordion",
              "bind": "renderingContextSettingsContainer",
              "label": "Context"
            }
          ]
        },
        "About": {
          "type": "panel",
          "bind": "about"
        }
      }
    }
  ]
}

VolumeLoadDialog:
{
  "type": "panel",
  "children": [
    {
      "type": "field",
      "label": "Type:",
      "children": [
        {
          "type": "dropdown",
          "bind": "type",
          "options": [
            {
              "value": "file",
              "label": "File",
              "selected": true
            },
            {
              "value": "url",
              "label": "URL"
            },
            {
              "value": "demo",
              "label": "Demo"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "filePanel",
      "visible": true,
      "children": [
        {
          "type": "field",
          "label": "File:",
          "children": [
            {
              "type": "file-chooser",
              "bind": "file"
            }
          ]
        },
        {
          "type": "panel",
          "bind": "rawSettingsPanel",
          "visible": false,
          "children": [
            {
              "type": "field",
              "label": "Dimensions:",
              "children": [
                {
                  "type": "vector",
                  "bind": "dimensions",
                  "value": 128,
                  "min": 1
                }
              ]
            },
            {
              "type": "field",
              "label": "Precision:",
              "children": [
                {
                  "type": "radio",
                  "bind": "precision",
                  "options": [
                    {
                      "value": 8,
                      "label": "8-bit",
                      "selected": true
                    },
                    {
                      "value": 16,
                      "label": "16-bit"
                    }
                  ]
                }
              ]
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "urlPanel",
      "visible": false,
      "children": [
        {
          "type": "field",
          "label": "URL:",
          "children": [
            {
              "type": "textbox",
              "bind": "url",
              "placeholder": "http://"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "demoPanel",
      "visible": false,
      "children": [
        {
          "type": "field",
          "label": "File:",
          "children": [
            {
              "type": "dropdown",
              "bind": "demo"
            }
          ]
        }
      ]
    },
    {
      "type": "panel",
      "bind": "loadButtonAndProgress",
      "visible": false,
      "children": [
        {
          "type": "field",
          "children": [
            {
              "type": "button",
              "bind": "loadButton",
              "label": "Load"
            }
          ]
        },
        {
          "type": "field",
          "children": [
            {
              "type": "progress-bar",
              "bind": "loadProgress"
            }
          ]
        }
      ]
    }
  ]
}
